// optimised
void fun(){
    int n;
    cin>>n;
    int input[n];
    for(int i=0;i<n;i++){
        cin>>input[i];
    }
    sort(input,input+n);
    for(int i=0;i<n;i++){
        cout<<input[i]<<" ";
    }cout<<endl;
    for(int i=0;i<n-2;i++){
        if(i==0 || (i>0 and input[i]!=input[i-1])){
            int sum=0-input[i];int j=i+1,k=n-1;
            while(j<k){
                if(input[j]+input[k] == sum){
                    cout<<input[i]<<" "<<input[j]<<" "<<input[k]<<endl;

                    while(j<k and input[j]==input[j+1]) j++;
                    while(j<k and input[k]==input[k-1]) k--;
                    j++;k--;
                }
                else if(input[j]+input[k] < sum){
                    j++;
                }
                else k--;

            }
        }
    }
}
    vector<vector<int>> threeSum(vector<int>& nums) {
        int n=nums.size();
        vector <vector<int>> ans;
        if(n<3) return ans;
        sort(nums.begin(),nums.end());
        for(int i=0;i<n;i++){
            cout<<nums[i]<<" ";
        }
        for(int i=0;i<n-2;i++){
            if(i==0 || (i>0 && nums[i]!=nums[i-1])){
                int a=0-nums[i];
                int l=i+1;int h=n-1;
                while(l<h){
                    if(nums[l]+nums[h] == a){
                        vector<int> temp;
                        temp.push_back(nums[i]);
                        temp.push_back(nums[l]);
                        temp.push_back(nums[h]);
                        ans.push_back(temp);
                        while(l<h && nums[l]==nums[l+1]) l++;
                        while(l<h && nums[h]==nums[h-1]) h--;
                        
                        l++;h--;
                    }
                    else if(nums[l]+nums[h] < a){
                        l++;
                    }
                    else h--;
                }
            }
        }
        return ans;
    }
